import styled from '@emotion/styled';

export const FriendsList = styled.ul`
  display: flex;
  flex-direction: column;
  align-items: center;
  vertical-align: middle;
  margin: auto;
  font-size: 28px;
  font-weight: 500;
  width: 80%;
`;
export const FriendItem = styled.li`
  margin: 12px;
`;

export const FriendStatus = styled.span
