import styled from '@emotion/styled';

export const TransactionTable = styled.table`
  width: 100%;
  margin: 20px;
  text-align: center;
  font-size: 20px;
  font-family: monospace;
`;

export const TransactionData = styled.td`
  padding: 12px;
`;
