//     gray: '#9e9e9e',
//     light: '#f2f2f2',
//     dark: '#212121',
// //   },

// // }



export const App = () => {
  return (
     <ThemeProvider>
        <h2>Task1</h2>
        <Profile
          username={user.username}
          tag={user.tag}
          location={user.location}
          avatar={user.avatar}
          stats={user.stats}
        />

        <h2>Task2</h2>
        <Statistics title="Upload stats" stats={data} />
        <Statistics stats={data} />

        <h2>Task3</h2>
        <FriendList friends={friends} />

        <h2>Task4</h2>
        <TransactionHistory items={transactions} />
       </ThemeProvider>
  );
};
