import styled from '@emotion/styled';

export const ProfileWrap = styled.div`
  width: 400px;
  margin: auto;
  font-size: 20px;
  font-family: sans-serif;
  box-shadow: 0 9px 47px 11px rgb(51 51 51 / 18%);
`;

export const ProfileImg = styled.img`
  max-width: 50%;
  height: auto;
  margin: auto;
  padding: 15px;
`;
